// screens/Home.js
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from "react-native";
import { useTransactions } from "../context/TransactionContext";

export default function Home({ navigation }) {
  const { totalIncome, totalExpense, balance, transactions } = useTransactions();

  return (
    <View style={styles.container}>
      <View style={styles.card}>
        <Text style={styles.balanceText}>Balance: ₹{balance}</Text>
        <Text style={styles.incomeText}>Income: ₹{totalIncome}</Text>
        <Text style={styles.expenseText}>Expense: ₹{totalExpense}</Text>
      </View>

      <View style={styles.actions}>
        <TouchableOpacity
          style={styles.btnIncome}
          onPress={() => navigation.navigate("AddIncome")}
        >
          <Text style={styles.btnText}>+ Add Income</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.btnExpense}
          onPress={() => navigation.navigate("AddExpense")}
        >
          <Text style={styles.btnText}>- Add Expense</Text>
        </TouchableOpacity>
      </View>

      <Text style={styles.sectionTitle}>Recent Transactions</Text>

      <FlatList
        data={transactions.slice(-5).reverse()}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <Text style={styles.title}>{item.title} ({item.category})</Text>
            <Text style={item.type === "income" ? styles.green : styles.red}>
              ₹{item.amount}
            </Text>
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: { padding: 20, flex: 1 },
  card: {
    padding: 20,
    backgroundColor: "#fff",
    borderRadius: 10,
    elevation: 3,
    marginBottom: 20,
  },
  balanceText: { fontSize: 20, fontWeight: "bold" },
  incomeText: { fontSize: 16, color: "green" },
  expenseText: { fontSize: 16, color: "red" },

  actions: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 20,
  },

  btnIncome: {
    backgroundColor: "green",
    padding: 12,
    borderRadius: 8,
    width: "48%",
  },
  btnExpense: {
    backgroundColor: "red",
    padding: 12,
    borderRadius: 8,
    width: "48%",
  },
  btnText: { color: "#fff", textAlign: "center", fontWeight: "bold" },

  sectionTitle: { fontSize: 18, fontWeight: "bold", marginBottom: 10 },

  item: {
    flexDirection: "row",
    justifyContent: "space-between",
    padding: 12,
    backgroundColor: "#fff",
    borderRadius: 8,
    marginBottom: 6,
    elevation: 2,
  },
  title: { fontSize: 16 },
  green: { color: "green", fontWeight: "bold" },
  red: { color: "red", fontWeight: "bold" }
});
