import { View, Text, StyleSheet, FlatList } from "react-native";

export default function Transactions() {
  // Temporary demo data – later we will connect backend
  const data = [
    { id: "1", type: "income", title: "Salary", amount: 12000, date: "2025-11-01" },
    { id: "2", type: "expense", title: "Groceries", amount: 1500, date: "2025-11-02" },
    { id: "3", type: "expense", title: "Travel", amount: 300, date: "2025-11-02" },
    { id: "4", type: "income", title: "Freelancing", amount: 4000, date: "2025-11-05" },
  ];

  const renderItem = ({ item }) => (
    <View style={styles.card}>
      <View>
        <Text style={styles.title}>{item.title}</Text>
        <Text style={styles.date}>{item.date}</Text>
      </View>

      <Text
        style={[
          styles.amount,
          item.type === "income" ? styles.income : styles.expense
        ]}
      >
        {item.type === "income" ? "+ " : "- "}₹{item.amount}
      </Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Transactions</Text>

      <FlatList
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={renderItem}
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#EAF3FF",
    paddingTop: 60,
    paddingHorizontal: 20,
  },

  header: {
    fontSize: 26,
    fontWeight: "bold",
    color: "#2A4D69",
    marginBottom: 20,
    textAlign: "center",
  },

  card: {
    backgroundColor: "#fff",
    padding: 18,
    borderRadius: 12,
    marginBottom: 15,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",

    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowRadius: 6,
    elevation: 3,
  },

  title: { fontSize: 18, fontWeight: "600", color: "#333" },
  date: { fontSize: 14, color: "#666", marginTop: 2 },

  amount: { fontSize: 20, fontWeight: "bold" },

  income: { color: "green" },
  expense: { color: "red" },
});
