// screens/AddIncome.js
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from "react-native";
import { useState } from "react";
import { useTransactions } from "../context/TransactionContext";

export default function AddIncome({ navigation }) {
  const { addTransaction } = useTransactions();

  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("Salary");

  const incomeCategories = ["Salary", "Business", "Freelancing", "Gift", "Other"];

  const save = () => {
    addTransaction(title, amount, "income", category);
    navigation.goBack();
  };

  return (
    <View style={styles.container}>
      <TextInput placeholder="Income Title" style={styles.input} onChangeText={setTitle} />

      <TextInput
        placeholder="Amount"
        style={styles.input}
        keyboardType="numeric"
        onChangeText={setAmount}
      />

      <Text style={styles.label}>Select Category</Text>

      {incomeCategories.map((c) => (
        <TouchableOpacity
          key={c}
          style={category === c ? styles.selected : styles.option}
          onPress={() => setCategory(c)}
        >
          <Text>{c}</Text>
        </TouchableOpacity>
      ))}

      <TouchableOpacity style={styles.btn} onPress={save}>
        <Text style={styles.btnText}>Save Income</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { padding: 20 },
  input: { borderWidth: 1, borderRadius: 8, padding: 10, marginBottom: 10 },
  label: { fontWeight: "bold", marginBottom: 8 },
  option: { padding: 10, marginVertical: 4, backgroundColor: "#eee", borderRadius: 8 },
  selected: { padding: 10, marginVertical: 4, backgroundColor: "#90ee90", borderRadius: 8 },
  btn: { backgroundColor: "green", padding: 14, borderRadius: 8, marginTop: 10 },
  btnText: { color: "#fff", textAlign: "center", fontWeight: "bold" }
});
