import { View, Text, TouchableOpacity, FlatList, StyleSheet } from "react-native";
import { useTransactions } from "../context/TransactionContext";

export default function Home({ navigation }) {
  const { transactions, totalIncome, totalExpense, balance } = useTransactions();

  const recent = [...transactions].slice(-5).reverse();

  return (
    <View style={styles.container}>

      {/* Balance Card */}
      <View style={styles.card}>
        <Text style={styles.balanceTitle}>Total Balance</Text>
        <Text style={styles.balanceAmount}>₹ {balance}</Text>

        <View style={styles.row}>
          <View style={styles.incomeBox}>
            <Text style={styles.label}>Income</Text>
            <Text style={styles.incomeAmount}>₹ {totalIncome}</Text>
          </View>

          <View style={styles.expenseBox}>
            <Text style={styles.label}>Expense</Text>
            <Text style={styles.expenseAmount}>₹ {totalExpense}</Text>
          </View>
        </View>
      </View>

      {/* Quick Actions */}
      <View style={styles.actions}>
        <TouchableOpacity 
          style={styles.incomeBtn}
          onPress={() => navigation.navigate("AddIncome")}
        >
          <Text style={styles.btnText}>+ Add Income</Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={styles.expenseBtn}
          onPress={() => navigation.navigate("AddExpense")}
        >
          <Text style={styles.btnText}>+ Add Expense</Text>
        </TouchableOpacity>
      </View>

      {/* Recent Transactions */}
      <Text style={styles.sectionTitle}>Recent Transactions</Text>

      <FlatList
        data={recent}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.transaction}>
            <View>
              <Text style={styles.transTitle}>{item.title}</Text>
              <Text style={styles.transCategory}>{item.category}</Text>
            </View>
            <Text style={item.type === "income" ? styles.incomeText : styles.expenseText}>
              {item.type === "income" ? "+ " : "- "}₹{item.amount}
            </Text>
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: "#fff" },

  card: {
    backgroundColor: "#f4f4f4",
    padding: 20,
    borderRadius: 12,
    marginBottom: 20,
  },

  balanceTitle: { fontSize: 18, fontWeight: "600", color: "#444" },
  balanceAmount: { fontSize: 32, fontWeight: "bold", marginVertical: 10 },

  row: { flexDirection: "row", justifyContent: "space-between" },

  incomeBox: { alignItems: "center" },
  expenseBox: { alignItems: "center" },

  label: { fontSize: 14, color: "#666" },

  incomeAmount: { fontSize: 20, color: "green", fontWeight: "600" },
  expenseAmount: { fontSize: 20, color: "red", fontWeight: "600" },

  actions: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 20,
  },

  incomeBtn: {
    backgroundColor: "green",
    padding: 10,
    borderRadius: 10,
    width: "48%",
    alignItems: "center",
  },

  expenseBtn: {
    backgroundColor: "red",
    padding: 10,
    borderRadius: 10,
    width: "48%",
    alignItems: "center",
  },

  btnText: { color: "#fff", fontWeight: "600" },

  sectionTitle: { fontSize: 18, marginBottom: 10, fontWeight: "600" },

  transaction: {
    flexDirection: "row",
    justifyContent: "space-between",
    backgroundColor: "#f0f0f0",
    padding: 12,
    marginVertical: 5,
    borderRadius: 10,
  },

  transTitle: { fontSize: 16, fontWeight: "500" },
  transCategory: { color: "#777", fontSize: 13 },

  incomeText: { color: "green", fontSize: 16, fontWeight: "bold" },
  expenseText: { color: "red", fontSize: 16, fontWeight: "bold" },
});
