import { View, Text, TouchableOpacity, StyleSheet } from "react-native";
import { useTransactions } from "../context/TransactionContext";

export default function HomeScreen({ navigation }) {
  const { balance, totalIncome, totalExpense } = useTransactions();

  return (
    <View style={styles.container}>
      <Text style={styles.title}>NariDhan Money Diary</Text>

      <View style={styles.balanceCard}>
        <Text style={styles.balanceLabel}>Total Balance</Text>
        <Text style={styles.balanceAmount}>₹ {balance}</Text>

        <Text style={styles.details}>Income: ₹ {totalIncome}</Text>
        <Text style={styles.details}>Expense: ₹ {totalExpense}</Text>
      </View>

      <TouchableOpacity
        style={styles.btn}
        onPress={() => navigation.navigate("AddIncome")}
      >
        <Text style={styles.btnText}>Add Income</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.btn}
        onPress={() => navigation.navigate("AddExpense")}
      >
        <Text style={styles.btnText}>Add Expense</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.btn}
        onPress={() => navigation.navigate("Transactions")}
      >
        <Text style={styles.btnText}>View Transactions</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, alignItems: "center", paddingTop: 80, backgroundColor: "#FFF" },
  title: { fontSize: 28, fontWeight: "bold", marginBottom: 30 },
  balanceCard: {
    width: "90%",
    padding: 20,
    borderRadius: 10,
    backgroundColor: "#f2f2f2",
    marginBottom: 30,
    alignItems: "center"
  },
  balanceLabel: { fontSize: 18 },
  balanceAmount: { fontSize: 32, fontWeight: "bold", marginTop: 5 },
  details: { fontSize: 16, marginTop: 5, color: "#555" },
  btn: {
    width: "90%",
    backgroundColor: "#4A90E2",
    padding: 15,
    borderRadius: 10,
    alignItems: "center",
    marginTop: 15
  },
  btnText: { color: "white", fontSize: 18 }
});
