import { View, Text, TextInput, TouchableOpacity, StyleSheet } from "react-native";
import { useState } from "react";
import { useTransactions } from "../context/TransactionContext";

export default function AddExpense({ navigation }) {
  const { addTransaction } = useTransactions();
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("Food");

  const categories = ["Food", "Travel", "Shopping", "Rent", "Health", "Other"];

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Add Expense</Text>

      <TextInput
        placeholder="Title"
        style={styles.input}
        value={title}
        onChangeText={setTitle}
      />

      <TextInput
        placeholder="Amount"
        style={styles.input}
        keyboardType="numeric"
        value={amount}
        onChangeText={setAmount}
      />

      {/* Category Picker */}
      <Text style={styles.label}>Category</Text>
      <View style={styles.categoryBox}>
        {categories.map((c) => (
          <TouchableOpacity
            key={c}
            onPress={() => setCategory(c)}
            style={[styles.catBtn, category === c && styles.catSelected]}
          >
            <Text style={styles.catText}>{c}</Text>
          </TouchableOpacity>
        ))}
      </View>

      <TouchableOpacity
        style={styles.btn}
        onPress={() => {
          addTransaction(title, amount, "expense", category);
          navigation.goBack();
        }}
      >
        <Text style={styles.btnText}>Add Expense</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { padding: 20, flex: 1 },
  heading: { fontSize: 22, fontWeight: "bold", marginBottom: 20 },

  input: {
    borderWidth: 1,
    borderColor: "#aaa",
    padding: 10,
    borderRadius: 10,
    marginBottom: 15,
  },

  label: { fontSize: 16, marginTop: 10, marginBottom: 5 },

  categoryBox: { flexDirection: "row", flexWrap: "wrap" },

  catBtn: {
    padding: 8,
    borderWidth: 1,
    borderColor: "#aaa",
    borderRadius: 8,
    margin: 5,
  },

  catSelected: { backgroundColor: "#ffdbdb", borderColor: "red" },

  catText: { fontSize: 14 },

  btn: {
    backgroundColor: "red",
    padding: 12,
    borderRadius: 10,
    marginTop: 20,
    alignItems: "center",
  },

  btnText: { color: "#fff", fontSize: 16, fontWeight: "600" },
});
