import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';

export default function HomeScreen({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Welcome to NariDhan & Money Diary</Text>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Diary')}>
        <Text style={styles.btnText}>Track Income & Expenses</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Education')}>
        <Text style={styles.btnText}>Financial Education</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Recommend')}>
        <Text style={styles.btnText}>Recommendations</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#f9f6f2' },
  title: { fontSize: 20, fontWeight: 'bold', color: '#4B0082', marginBottom: 20, textAlign: 'center' },
  button: { backgroundColor: '#7a42f4', padding: 15, margin: 10, borderRadius: 10, width: '80%' },
  btnText: { color: '#fff', textAlign: 'center', fontSize: 16 }
});
